package placements;

public class Grade {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
			  int marks = 80;
			  
			  if (marks >= 91) {
			    System.out.println("A");
			  } else if (marks >= 76 && marks <= 90) {
			    System.out.println("B");
			  } else if (marks >= 61 && marks <= 75) {
			    System.out.println("C");
			  } else {
			    System.out.println("D");
			  }
			}


	}


